<?php// Include Configuration Fileif (file_exists("../configuration.php")) {  require_once("../configuration.php");} else {  echo "Missing Configuration File";  exit();}// Include Language Fileif (file_exists("../languages/lang." . $ariaConfig_language . ".php")) {  require_once("../languages/lang." . $ariaConfig_language . ".php");} else {  echo "Missing Language File";  exit();}// Include Params Fileif (file_exists("../params/params." . $ariaConfig_language . ".php")) {  require_once("../params/params." . $ariaConfig_language . ".php");} else {  echo "Missing Params File";  exit();}// Include Database Controllerif (file_exists("../include/database.php")) {  require_once("../include/database.php");} else {  echo "Missing Database File";  exit();}// Include System Fileif (file_exists("../include/ariacms.php")) {  require_once("../include/ariacms.php");} else {  echo "Missing System File";  exit();}// Set file sendmailif (file_exists("../plugins/phpmailer/class.phpmailer.php")) {  require_once("../plugins/phpmailer/class.phpmailer.php");} else {  echo "Missing Mailer File";  exit();}/* Setup Global variables */$database = new database($ariaConfig_server, $ariaConfig_username, $ariaConfig_password, $ariaConfig_database);$ariacms = new ariacms();$params = new params();$key =  @$_REQUEST['key'];$row =  @$_REQUEST['row'];$allcount =  @$_REQUEST['allcount'];//$query = "SELECT * FROM e4_posts WHERE MATCH (title_vi,brief_vi,content_vi) AGAINST ('$key' IN NATURAL LANGUAGE MODE) ORDER BY id desc limit $row,10";$query = "SELECT * FROM e4_posts WHERE (title_vi like '%$key%' or brief_vi like '%$key%' or content_vi like '%$key%') and aproved_date <".time()." ORDER BY id desc LIMIT '$row',10";$database->setQuery($query);$taxonomies = $database->loadObjectList();	if(!$ariacms->checkUserLogin()){	$lightbox = "uk-toggle";	$url_login = '#modal-login';	$url_post = '#modal-login';}else{	$lightbox = "";	$url_login = "javascript:;";	$url_post = '/member/tao-bai-viet.html';}$query = "select post_id  from e4_post_like where member_id = ".$_SESSION["member"]['id']." ORDER BY id desc";$database->setQuery($query);$member_likes = $database->loadObjectList();$array_liked = array();foreach($member_likes as $data_post){	array_push($array_liked,$data_post->post_id);}$array_liked = array_flip($array_liked);foreach($taxonomies  as $post){	$like="";	if($array_liked[$post->id] > -1){		$like = 'style="color: blue;"';	}	?>	<div class="lg:flex lg:space-x-6 py-6">		<a href="<?=$ariacms->actual_link . 'chi-tiet/' . $post->url_part . '.html';?>">			<div class="lg:w-60 w-full h-40 overflow-hidden rounded-lg relative shadow-sm"> 				<img src="<?=$post->image?>" alt="" class="w-full h-full absolute inset-0 object-cover">				<div class="absolute bg-blue-100 font-semibold px-2.5 py-1 rounded-full text-blue-500 text-xs top-2.5 left-2.5">					<i class="spr spr__clock"></i><?php if($post->post_created > 0){ echo $ariacms->unixToDate($post->post_created, '/') .' '. $ariacms->unixToTime($post->post_created, ':') ;}?>				</div>			</div>		</a>				<div class="flex-1 lg:pt-0 pt-4">			<a href="<?=$ariacms->actual_link . 'chi-tiet/' . $post->url_part . '.html';?>" class="text-xl font-semibold line-clamp-2">  <?=$post->title_vi?></a>			<p class="line-clamp-2 pt-1"> <?=$post->brief_vi)?></p>						<div class="flex items-center pt-3">				<div class="flex items-center" id="like<?=$post->id?>">  					<a href="<?=$url_login;?>" <?=$lightbox?> <?php if($url_login == 'javascript:;'){ ?> onclick="likepost('<?=$post->id?>')" <?php } ?> class="items-center"><ion-icon name="thumbs-up-outline" class="text-xl mr-2 md hydrated" role="img" aria-label="thumbs up outline" <?= $like ?>></ion-icon>  <?php echo $post->number_like ?> </a>				</div>				<div class="flex items-center mx-4" > 					<a href="<?=$url_login;?>" <?=$lightbox?> <?php if($url_login == 'javascript:;'){ ?> onclick="commentpost('<?=$post->id?>')" <?php } ?> class="items-center">					<ion-icon name="chatbubble-ellipses-outline" class="text-xl mr-2 md hydrated" role="img" aria-label="chatbubble ellipses outline"></ion-icon>  <?php echo $post->number_comment ?> 					</a>				</div>			</div>		</div>	</div><?php 	} 			?> 	